int bsp_init(uint32_t u32_type, bsp_event_callback_t callback)
{
    int i32_rval = 0;

    do
    {
        if (u32_type & BSP_INIT_LEDS)
        {
            bsp_led_init(callback);
        }
        if (u32_type & BSP_INIT_BUTTONS)
        {
            bsp_button_init(callback);
        }
        if (u32_type & BSP_INIT_UART)
        {
            bsp_uart_init(callback);
        }

    } while (0);

    return i32_rval;
}
